import {Link} from "react-router-dom"
import { IoCloseSharp,IoFlagSharp ,IoSendSharp} from "react-icons/io5";
import { AiOutlinePlus } from "react-icons/ai";
class Chat extends Component {
    state={
chat:[]
    }
    render(){
        return (
            <div className="char0-box-class">
            <div id="chat-box">
             <div id="head-chat">
        <button className="close-chat">
      <IoCloseSharp/>
       </button>
       <p>CHAT ROOM</p>
        </div>
       <div id="chat-content">  
       {this.state.chat?.map((item,index)=>{
           return(
            <div key={index} className="char-room-box">
           <div className="icon"></div>
           <div className="wraprtt">
               <div className="name-people">Kenneth</div>
               <div className="conversationt"> 
               {item.message}
               </div>
           </div>
       </div>
           )
       })}

       </div> 
       <div className="type-message-box">
				<div className="watpr-contnr-mem">
					<div className="wrappe-mmeshe">
					<div contentEditable="true" onKeyUp={this.addMessage} data-placeholder="Type a message..." className="hold-message noped"></div>
                    <div className="send-hold fnjjrjr"><button onClick={this.sendmessage} ><IoSendSharp/></button></div>
				</div>
				</div>
			</div>
            </div>

            </div>
        )
    }
}
    export default Chat
