import React,{Component} from "react"
import {withRouter} from "react-router-dom"
import Chat  from "../component/chat"
import question  from "../question"
import {connect} from "react-redux"
import socket from "../socketconf";
import Leaderboard from "../component/leaderboard"
let time=null
class Create extends Component {
    state={
        currentUser:0,
        message:true,
        game:false,
        leaderboard:false,
        awnser:"5",
        currentCount:8,
        correctAwnser:"5",
        liveCounter:10,
        check:false,
        currentQuestion:{},
        currentScore:0,
        displayCount:false,
        realTimeLeaderboardState:{}
    }
realTime=()=>{
socket.on("beginTheGame",()=>{
this.setState({
    displayCount:true  
})
setTimeout(()=>{
this.setState({
    game:true,
    message:false
},()=>{
    this.startTheGame()
})
},3000)
})
socket.on("update-score",(data)=>{
    console.log(data)
let curretScore= this.state.realTimeLeaderboardState
if(curretScore[data.name]){
    curretScore[data.name]= Math.max(curretScore[data.name],data.score)
    this.setState({
        realTimeLeaderboardState:curretScore
    })
}else{
    curretScore[data.name]=data.score
    this.setState({
        realTimeLeaderboardState:curretScore
    })
}
})
}

playTheGame=()=>{
    console.log(this.props.roomId)
socket.emit("start-game",this.props.roomId)
}
 buildCounter=(duration)=> {
        var timer = duration, minutes, seconds;
        minutes = parseInt(timer / 60, 10);
        seconds = parseInt(timer % 60, 10);
        minutes = minutes < 10 ? "0" + minutes : minutes;
        seconds = seconds < 10 ? "0" + seconds : seconds;

        //display.textContent = minutes + ":" + seconds;

        if (--timer < 0) {
        return "00:00"
        }else{
            return minutes + ":" + seconds
        }


    }
    componentWillUnmount=()=>{
        socket.off("beginTheGame")
    }
    componentDidMount=()=>{
        //if(!this.props.connected)this.props.history.push("/")
        this.setState({
            roomId:this.props.roomId ,
            currentQuestion:question[this.state.currentCount],
        })
        this.realTime()
    }
    checkAwnser=()=>{
        this.setState({
            check:true,
            correctAwnser:this.state.currentQuestion.awnser
        })
        if(this.state.awnser!=="5"){
            if(this.state.awnser===this.state.correctAwnser){
                let curentScore= this.state.currentScore
                curentScore+=10
                this.setState({
                  currentScore:curentScore
                })
              } 
        }else{

        }

      setTimeout(()=>{
        this.setState({
            liveCounter:10
        },()=>{

            socket.emit('new-score',{room:this.props.roomId,name:this.props.username,score:this.state.currentScore})
            this.moveNext()
            if(this.state.currentCount+1!==question.length){
                this.callTheGame()
            }

        })
      },5000)
    }
showLeaderBoard=()=>{
let currentLeaderBoard=Object.entries(this.state.realTimeLeaderboardState)
console.log(currentLeaderBoard)
}
    moveNext=()=>{
        if(this.state.currentCount+1!==question.length){
            this.setState({
                awnser:"5",
                correctAwnser:"5",
                check:false,
                currentCount:this.state.currentCount+1,
                currentQuestion:question[this.state.currentCount+1]
            })
        }else{
            this.showLeaderBoard()

        }

    }
    handleClick=(e)=>{
  this.setState({
    awnser:e.target.id,
    correctAwnser:this.state.currentQuestion.awnser
  })
    }

    callTheGame=()=>{

        time=setInterval(()=>{
            if(this.state.liveCounter===1){
                clearInterval(time)
                this.setState({
                    liveCounter:0
                },()=>{
                    this.checkAwnser()
                })
            }else{
                this.setState({
                    liveCounter:this.state.liveCounter-1
                })
            }
        },1000)
    }

  startTheGame=()=>{
this.callTheGame()
  }
    render(){
        return (
            <div className="createe-user">
                 <div id="containerr"> 
                  <div id="createe"> 
                  <div id="back-page">
                  <i className="fas fa-chevron-left">
                  </i></div> 
                    </div>
                  {this.state.message? <div id="message">
                    <header id="header">
                    <p id="numer-client">{this.state.currentUser}</p>
                    <p id="user-game-name">Computer Science</p>
                    </header>
                    <div id="code">
                    <p id="message-game">Click start when you are ready</p>
                    <div id="box-room">
                    <p id="h3">Room Id</p><p id="room-id">{this.state.roomId}</p>
                    <button onClick={this.playTheGame} className="butttin-action" >Start</button>
                    </div>
                    </div><div id="ready-box"><p id="text-redy">3</p></div>
                    <div id="overlay"></div>
                    <div id="people-connected">
                    <div className="closes"><p className="fas fa-times"></p></div>
                    <div id="test-descrb-people">Player </div>
                    <div id="title-name-conected"></div>
                    <div id="list"><div className="list-people">
                    <i id="ico" className="far fa-user-circle"></i>
                    <div id="name-connected"> 
                    <p id="name-user-conec">Kenneth</p> 
                    <p id="host"></p>
                    </div> 
                    </div></div>
                    </div>
                    </div>:""}
                    {this.state.game? <div id="game">
                    <header id="head">
                    <p id="online-user">Kennbbth</p>
                    <div id="point">
                    <p id="coin">{this.state.currentScore}</p>
