import React,{Component} from "react"

import socket from "../socketconf"

class EmailBox extends Component{
state={
    email:"",
    message:"",
    good:false,
    loading:false
}
handoeReatime=()=>{
   socket.on("email-created",data=>{
     if(data.email){
        this.setState({
            message:"Congratulations, you are all set ðŸ˜„",
            good:true,
            loading:false
           })

     }else{
        this.setState({
            message:"You already subscribe to our Newsletter ",
            good:false,
            loading:false
           })
     }

   })
}

handleClick=()=>{
const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
if(re.test(String(this.state.email).toLowerCase())){

    this.setState({
        loading:true
    })
    socket.emit("email-send",this.state.email)
}else{
    this.setState({
        message:"Please format you email Correctly",
        good:false
    })
}
}

handleSave=(event)=>{
    this.setState({
        email:event.target.value
    })
}

componentDidMount=()=>{
    this.handoeReatime()
}
    render(){
        return(
            <div className={`box-the-hoks-rth-subfb ${this.props.top==true?"active":""}`}>
            <input onChange={this.handleSave}  placeholder="Enter your email..."  type="email"/>
            <button className={`fjnjntj ${this.state.loading==true?"active":""}`} onClick={this.handleClick}>
                <p>SUBSCRIBE</p>
{this.state.loading==true?<div class="lds-ring"><div></div><div></div><div></div><div></div></div>:""}    
            </button>
            <p className={`fjfnjj ${this.state.good==true?"green":"red"}`}>{this.state.message}</p>
        </div>
        )
    }
}

export default EmailBox
