import logo from "../logo/logo.png"
import iphone from "../image/iphone.png"
import { BiLockAlt,BiDollar } from "react-icons/bi";
import { BsFillPeopleFill,BsInfoSquare,BsSun} from "react-icons/bs";
import Feature from "./feature"
import React,{Component} from "react"
import About from "./about"
import video1 from "../image/video1.mp4"
import Fouter from "./fouter"
import Slide from "./silde"
import Nav from "../component/nav"
import Team from "./team"
import Editable from "../component/editable"
import { AiFillHome } from "react-icons/ai";
import { VscChromeClose } from "react-icons/vsc";
import Info from "./info"
import { GiHamburgerMenu } from "react-icons/gi";
import Subscribe from "./subscibebox"
import EmailBox from "../component/emailbox"
import { FaRegMoon ,FaMoon} from "react-icons/fa";
import { RiTodoLine ,RiTeamLine} from "react-icons/ri";
import { FiInstagram,FiTwitter,FiFacebook } from "react-icons/fi";
import { IoIosNotifications } from "react-icons/io";


class Contact extends Component{
    state={
        mode:false,
        nav:false,
        email:"",
        fullName:"",
        Subject:"",
        content:"",
        loading:false,
        messageone:"",
        messagetwo:"",
        messagethree:"",
        messagefour:"",
        succes:false
    }


    handleChange=(event)=>{

      if(event.target.name=="fullName"){
        this.setState({
          fullName:event.target.value
        })
      }
      if(event.target.name=="Email"){
        this.setState({
          email:event.target.value
        })
      }
      if(event.target.name=="Subject"){
        this.setState({
          Subject:event.target.value
        })

      }

    }

    handleBio=(event)=>{
      this.setState({
        content:event.target.textContent
      }) 
    }
    switchToggle=()=>{
        this.setState({
          mode:!this.state.mode
        })
        localStorage.setItem('mode', !this.state.mode);
      }
      openNav=(data)=>{
        this.setState({
          nav:data
        })
      }


      sendemail=()=>{
        if(this.state.fullName.length>0){
          this.setState({
            messageone:""
          })

        }else{
          this.setState({
            messageone:"Field can not be empty "
          })
        }

        if(this.state.email.length>0){
          this.setState({
            messagetwo:""
          })
        }else{
          this.setState({
            messagetwo:"Field can not be empty "
          })
        }

        if(this.state.Subject.length>0){
          this.setState({
            messagethree:""
          })
        }else{
          this.setState({
            messagethree:"Field can not be empty "
          })
        }


        if(this.state.content.length>0){
          this.setState({
            messagefour:""
          })

        }else{
          this.setState({
            messagefour:"Field can not be empty "
          })
        }


        if(this.state.email.length>0 &&this.state.fullName.length>0&& this.state.content.length>0 &&this.state.Subject.length>0 ){
let option ={
  email:this.state.email,
  fullName:this.state.fullName,
  Subject:this.state.Subject,
  content:this.state.content
}
        }


      }
    componentDidMount=()=>{
        const mode = localStorage.getItem('mode') === 'true';
      this.setState({mode:mode});

      }
    render(){
        return(
            <div className={`contact-page ${this.state.mode==true?"active":""}  `}>
            <div className="whrjhrr">
            <Nav  switchToggle={this.switchToggle}  openNav={this.openNav}/>

<div className="wraparirir">
<div className="backsjiri">
  Contact us
</div>
<div className={`wisfirj  ${this.state.mode==true?"active":""}`}> 
<div className="wroaiororr"> 
<div className="edit-box-profile">
<p>Full name</p>
<input onChange={this.handleChange}   className="fullname-profile" type="text" name="fullName" placeholder={""}  />
</div>
<p className="wrrk">{this.state.messageone}</p>
</div>
<div className="wroaiororr"> 

<div className="edit-box-profile">
<p>Email</p>
<input onChange={this.handleChange}   className="fullname-profile" type="text" name="Email" placeholder={""}  />
</div>
<p className="wrrk">{this.state.messagetwo}</p>

</div>

<div className="wroaiororr"> 
<div className="edit-box-profile">
<p>Subject</p>
<input onChange={this.handleChange}   className="fullname-profile" type="text" name="Subject" placeholder={""}  />
</div>
<p className="wrrk">{this.state.messagethree}</p>
</div>
<div className="wroaiororr"> 
<div className="edit-box-profile">
<p>What is on your mind?</p>

<Editable handleBio={this.handleBio} html={""}/>
</div>
<p className="wrrk">{this.state.messagefour}</p>
</div>
</div>
<div onClick={this.sendemail} className={`wraparjrjrj ${this.state.loading==true?"active":""}`}>
<button  className={`fjnjntj ${this.state.loading==true?"active":""}`} onClick={this.handleClick}>
     <p>{this.state.loading==false?"SEND EMAIL":"SENDING EMAIL"}</p>
     <div className="frrfjrj">
    <div className="suwuhshhd">
    {this.state.loading==true?<div class="lds-ring"><div></div><div></div><div></div><div></div></div>:""}
    </div>
     </div>

 </button>
</div>
</div>

            </div>
            <Fouter/>  
            </div>

        )
    }
}

export default Contact
