import iphone from "../image/iphone.png"
import { BiLockAlt,BiDollar } from "react-icons/bi";
import { BsFillPeopleFill,BsInfoSquare,BsSun} from "react-icons/bs";
import Feature from "./feature"
import React,{Component} from "react"
import About from "./about"
import video1 from "../image/video1.mp4"
import Fouter from "./fouter"
import Slide from "./silde"
import Typing from 'react-typing-animation';
import Nav from "../component/nav"
import Team from "./team"
import Fade from 'react-reveal/Fade';
import { AiFillHome } from "react-icons/ai";
import { VscChromeClose } from "react-icons/vsc";
import Info from "./info"
import { GiHamburgerMenu } from "react-icons/gi";
import Subscribe from "./subscibebox"
import EmailBox from "../component/emailbox"
import Reveal from 'react-reveal/Reveal';
import { FaRegMoon ,FaMoon} from "react-icons/fa";
import { RiTodoLine ,RiTeamLine} from "react-icons/ri";
import { FiInstagram,FiTwitter,FiFacebook } from "react-icons/fi";
import { IoIosNotifications } from "react-icons/io";


class  Home extends Component {
  state={
    nav:false,
    mode:false,

  }

  switchToggle=()=>{
    this.setState({
      mode:!this.state.mode
    })
    localStorage.setItem('mode', !this.state.mode);
  }
  openNav=(data)=>{
    this.setState({
      nav:data
    })
  }
  componentDidMount=()=>{
    const mode = localStorage.getItem('mode') === 'true';
  this.setState({mode:mode});



  }
  render(){
  return (
    <div className={`homne-page ${this.state.mode==true?"active":""}  `}>
<div className={`jetjntj ${this.state.mode==true?"active":""}  `}>
<Nav  switchToggle={this.switchToggle}  openNav={this.openNav}/>

<div className="holsnjnfj-iofjf">
<div className="hold-theicondjfnnr">
  <div className="wrspjjjrrork">
<div className="hold-iphomje">
<img draggable="false" src={iphone}/>
</div>
<div className="hold-logo">
<img draggable="false" src={logo}/>
</div>
  </div>
</div>
<div className="fjsfieiitwire">


<div className={`hfgjjetrnrn ${this.state.mode==true?"active":""}  `}>
<div className="trrjsjjrr">
Surpass Your Limit
</div>
<div className="smajkkrjr"> A mobile app and web applications that assist you with your fitness related activities and help you earn money from the comfort of your own home.</div>
<EmailBox top={true}/>

</div>
</div>

</div>
    </div>
    <Fade left>
    <div className="box-bnsjrkrjk">
Who need a website when you can have a fitness world
</div>
        </Fade>



  <div id="about"   className="wrpsjrtjjt-ntjj">
  <Info mode={this.state.mode}/>
  <Feature mode={this.state.mode}/>
  </div>
<Slide/>
<About mode={this.state.mode}/>
<Team mode={this.state.mode}/>
<Subscribe mode={this.state.mode}/>
<Fouter mode={this.state.mode}/>

    </div>
  );
  }
}

export default Home;
